INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Cube of Meat', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/40/Cube_of_Meat_Icon.png?version=339a163ab79db43e045c31d923712d85', 'Gotta meat \'em all!', 'Orbiting familiar that blocks enemy shots and deals damage to enemies. If collected while already having one, makes the familiar evolve. Only drops from Horsemen. Similar to Ball of Bandages');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'A Quarter', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/c3/A_Quarter_Icon.png?version=7c935cc9a1cfe9b6332247878c28cc89', '+25 coins', 'Adds 25 to Isaac\'s coin count, capping at 99');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'PHD', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/ec/PHD_Icon.png?version=07224e018ee0e2d12c48f7878eb0e8e3', 'Better pills', 'Converts all bad pills into good pills, spawns one pill pickup and restores health. Identifies all pills upon pickup');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'X-Ray Vision', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/0/00/X-Ray_Vision_Icon.png?version=92494906440ea3a7a75178514e67cfed', 'I\'ve seen everything', 'Reveals and opens all Secret Room and Super Secret Room entrances');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'My Little Unicorn', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/cb/My_Little_Unicorn_Icon.png?version=f0b85a7a32c02383570b066bf6704298', 'Temporary badass', 'Upon use, grants invulnerability and increased speed for five seconds, removing the ability to shoot tears and replacing it with the ability to deal contact damage to enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Book of Revelations', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/45/Book_of_Revelations_Icon.png?version=29fe7a1a8b6778f57317a72882bbffd2', 'Reusable soul protection', 'Adds 1 soul heart, increases the chance for a Devil or Angel Room to appear, and increases the chance for the next boss to be a Horseman (except on XL floors and floors with fixed bosses such as Mom)');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Mark', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b3/The_Mark_Icon.png?version=70da36baf3078c50dda7656c06bbe7bb', 'DMG up', 'Increases Damage and Speed, plus adds one Soul Heart');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Pact', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/3a/The_Pact_Icon.png?version=18c5caef06502fb41ff0f5144107306b', 'DMG + Tears UP', 'Increases damage by 0.5, tears by 0.7 and grants two soul hearts upon pickup');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Dead Cat', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/27/Dead_Cat_Icon.png?version=57af3b78731e631445ceca4d25307994', '9 lives', 'Sets the amount of red heart containers to 1, but gives Isaac 9 extra lives. After dying, Isaac is teleported to the last safe room. New heart containers can be obtained after picking up this item, but after each death, the number of heart containers is set to 1 again');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lord of the Pit', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e5/Lord_of_the_Pit_Icon.png?version=72b7269dc07a86bb92a1b42819e1fa23', 'Demon wings', 'Grants flight and increases speed');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Nail', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e0/The_Nail_Icon.png?version=013e7cdfd794c7b8f86854fef2bd2fca', 'Temporary demon form', 'Adds one soul heart and allows the breaking of rocks and damaging enemies by simply walking over them. Increases damage and decreases speed. All effects except for the Soul Heart wear off upon leaving the room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'We Need to Go Deeper!', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/69/We_Need_to_Go_Deeper%21_Icon.png?version=e6965ea478f91f1387e5e77d50a98028', 'Reusable level skip', 'Creates a trapdoor to go to the next floor. If used in The Womb, it will create a trapdoor to Sheol, even if the player hasn\'t unlocked it');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Deck of Cards', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/14/Deck_of_Cards_Icon.png?version=da4e9fe7dd4048523e4ec7fc2cd1ff08', 'Reusable Card Generator', 'Gives Isaac a random Tarot Card');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Monstro\'s Tooth', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/76/Monstro%27s_Tooth_Icon.png?version=66a033efd967eb0d4a85f11fb10bc8a5', 'Summon Monstro', 'Monstro jumps onto a random enemy, doing area damage. Can damage/kill the player if summoned in an room without enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Loki\'s Horns', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/2c/Loki%27s_Horns_Icon.png?version=c21ea8c779129ab3d89210fc58b710bc', 'Cross Tears', 'Gives a random chance for projectiles to fire in all 4 cardinal directions');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Little Chubby', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/61/Little_Chubby_Icon.png?version=c440f6472b1f36bc6ed3db02ed25c30a', 'Attack Buddy', 'A familiar that charges forward and deals touch damage to any enemies in its path');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Spider Bite', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/65/Spider_Bite_Icon.png?version=c225b4393314f6651749544d4acccbcf', 'Slow effect', 'Adds a chance to shoot slowing tears, reducing speed of enemy movement and any projectile fired by them');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Small Rock', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/61/The_Small_Rock_Icon.png?version=aa6aaef0b781844c57d75f4f34a8ab24', 'DMG up', 'Increases damage by 1, increases tears by 0.2, and decreases speed by 0.1');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Spelunker Hat', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/af/Spelunker_Hat_Icon.png?version=deabc118b09788561354232776461058', 'See-through doors', 'Reveals type of adjacent rooms, including location for secret rooms and super secret rooms');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Super Bandage', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/20/Super_Bandage_Icon.png?version=7e104e210ea2353519b8e134498bb6b9', '+2 hearts', 'Grants one Heart Container and gives 2 Soul Hearts');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Gamekid', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/5e/The_Gamekid_Icon.png?version=da0921d24563b7f6c9a85fc077b4bc33', 'Temporary Man-Pac', 'Upon use, turns the character into a into a Pac-Man-like creature for 5 seconds, granting invulnerability, inflicting fear on all enemies, and replenishing half a red heart container for every two enemies killed');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Sack of Pennies', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e6/Sack_of_Pennies_Icon.png?version=6a3bd8b98ad6edf87313b97682814296', 'Gives money', 'Isaac is followed by a small bag that drops a coin every 2 rooms');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Robo-Baby', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/77/Robo-Baby_Icon.png?version=a02603608308f5dd7add3fab9b30790e', 'Friends \'till the bbbbzzzt', 'Follows Isaac around, shooting enemies with an eye laser similar to Technology');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Little C.H.A.D.', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/1b/Little_C.H.A.D._Icon.png?version=ef5cb653022ff6779fb65e809ce372ae', 'Gives kisses', 'A familiar who will drop a half red heart every 2 rooms');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Book of Sin', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/8f/The_Book_of_Sin_Icon.png?version=6e526ce67055bf0c969a3b6c5de92eec', 'Reusable Item Generator', 'Spawns a random pickup, pill, card, or trinket');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Relic', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/bb/The_Relic_Icon.png?version=8f0cf55ddc72dc79750c0f12cad01528', 'Soul generator', 'Isaac is followed by a small blue cross that will drop a Soul Heart every 4 rooms');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Little Gish', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e8/Little_Gish_Icon.png?version=973c6383ab3d3ed9b084280fbdf3be25', 'Sticky friend', 'Spawns a familiar that follows Isaac around and fires slowing tears, impeding enemies\' speed');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Little Steven', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/3c/Little_Steven_Icon.png?version=eecda5e5ff4d2a03c9b8bb633c8d4ffd', 'Psychic friend', 'Spawns a familiar that follows Isaac around and fires homing tears');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Halo', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/20/The_Halo_Icon.png?version=d8406ae02845ee15b1d27400442553f9', 'All stats up', 'Grants one Heart Container, +0.3 Damage, +0.2 Tears, +0.25 Range, +0.3 Speed, and +0.5 Tear Height');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mom\'s Bottle of Pills', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d4/Mom%27s_Bottle_of_Pills_Icon.png?version=564ba5a6244237f07e33e3d8211f8dc7', 'Reusable pill generator', 'Gives Isaac a random pill, and will cause him to drop any held Q-slot item (If any). The player will only receive six different pill types in one playthrough');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Common Cold', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/6b/The_Common_Cold_Icon.png?version=56730ef9a7a06c901f0f505529bba945', 'Poison Damage', 'Adds a chance to shoot poison tears, dealing damage over time when inflicted');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Parasite', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/7d/The_Parasite_Icon.png?version=1a27a7219792195a95f1be24778fdc57', 'Split Shot', 'Isaac\'s tears split in two when they hit something');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The D6', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/fa/The_D6_Icon.png?version=6ad5afef39a5154980549130216689ba', 'Reroll Your Destiny', 'Allows Isaac to re-roll collectible items in the current room into other items, depending on the current room\'s Item Pool');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mr. Mega', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/27/Mr._Mega_Icon.png?version=e5c793aa1b8b9656b1104e9cc2bf4f8b', 'Blast damage', 'Gives Isaac 5 bombs, and makes all dropped bombs larger, which have an increased blast radius and increased damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Pinking Shears', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/4b/The_Pinking_Shears_Icon.png?version=848f4aa4ca8ba186a75e12976ab58ca1', 'Cut and Run', 'Separates Isaac\'s head and body for the current room. Isaac\'s head, which now has flight, will be under the player\'s control, while his body will seek out enemies to attack by contact damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Wafer', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/97/The_Wafer_Icon.png?version=c21198cdd4deaf533ec629e60e40b37b', 'Damage resistance', 'All sources of damage that would cause more than one half heart of damage are reduced to one half heart instead');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Money = Power', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e7/Money_%3D_Power_Icon.png?version=1265daa3c722327d5b00b5d4f9718a9a', '$$$ = DMG', 'Increases damage by 0.04 for every coin Isaac has, up to +3.96 damage at 99 coins');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mom\'s Contacts', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/ad/Mom%27s_Contacts_Icon.png?version=c8615c83a9ef32513eadcb3859aa8efd', 'Freeze effect', 'Adds a chance to shoot petrifying tears, which stop enemies in place and prevent them from moving or attacking. Range up');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Bean', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b2/The_Bean_Icon.png?version=c511fcc6233c1ec7dc395c72e7003d21', 'Toot on command', 'Isaac releases bad gas, poisoning any enemy in close vicinity');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Guardian Angel', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/f6/Guardian_Angel_Icon.png?version=aad3640ed7d49eb424bdd2fb84e4ca1c', 'Extra protection', 'Spawns an orbital familiar that blocks enemy projectiles, deals contact damage to enemies, and increases the speed of all orbital familiars');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Demon Baby', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a6/Demon_Baby_Icon.png?version=390e6e9945e7a7482736e3e0533b9058', 'Auto-turret friend', 'Follows Isaac and automatically fires at enemies in its reach');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mom\'s Knife', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/bf/Mom%27s_Knife_Icon.png?version=a0861d70279babf1524d4f8d019312b3', 'Stab stab stab', 'Isaac\'s tears are replaced by a knife that pierces enemies, can travel through obstacles, and can hurt enemies while Isaac is holding it. Holding down the fire button before firing increases its range and power');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Ouija Board', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e5/Ouija_Board_Icon.png?version=b96c8ed715b0d3fafd655f17b12f1d45', 'Spectral tears', 'Grants spectral tears that travel through obstacles (but not enemies) instead of breaking on impact with them');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', '9 Volt', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/56/9_Volt_Icon.png?version=a1ee456240ecb0e6439b02786b968b65', 'Quicker charge', 'Reduces the charge of any Activated Items by 1. Any 1 charge items instead have timed recharges');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Dead Bird', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/13/Dead_Bird_Icon.png?version=7360c17882fe22cd1f2e65b3fe1fcc18', 'Protective buddy', 'Spawns a flying familiar for the current room that chases enemies whenever Isaac takes damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Brimstone', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/c8/Brimstone_Icon.png?version=ed6d8b423e50a1fdb6b607f1a3180440', 'Blood laser barrage', 'Tears are replaced by a laser beam that pierces through all enemies and obstacles in its path and deals high damage. Can only be fired after a short charge-up period');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Blood Bag', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/29/Blood_Bag_Icon.png?version=ccaea0ce2e88674b181f6bbc12683ae1', 'HP up', 'Grants one red heart container, restores 5 red hearts, and increases speed by 0.3');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Odd Mushroom', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/36/Odd_Mushroom_%28Thin%29_Icon.png?version=20ed49086899fd1c669f6d8804afdc1e', 'Fire rate up', '+1.7 tears, +0.3 speed, reduces damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Odd Mushroom', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/69/Odd_Mushroom_%28Large%29_Icon.png?version=0bcc972d554f2ba9fa5a457e47c4ae3b', 'DMG up', 'Grants an empty heart container. Increases damage, range and shot height while reducing speed');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Whore of Babylon', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/dc/Whore_of_Babylon_Icon.png?version=8affe45477abe5c19f9ae2a6de035551', 'Curse up', 'Increases damage and speed whenever Isaac has half a red heart or less (one red heart for Eve), ignoring any soul or black hearts');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Monster Manual', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/9c/Monster_Manual_Icon.png?version=e1a3a87ee8d78ca45ea92b36eefb0e75', 'Temporary buddy generator', 'Summons a random familiar for the current room. Can summon familiars not yet unlocked');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Dead Sea Scrolls', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/42/Dead_Sea_Scrolls_Icon.png?version=5299eb28614335fc61563d9004c8234c', 'It\'s a mystery', 'Triggers the effect of a random activated item from a list of possible options (not every activated item is included)');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Bobby-Bomb', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d3/Bobby-Bomb_Icon.png?version=051f7b0ff72b37317ab26707edff85d3', 'Homing bombs!', 'Gives Isaac 5 Bombs, and causes all dropped bombs to home towards the closest enemy');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Razor Blade', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b4/Razor_Blade_Icon.png?version=9450cedf6b6d505ebe1ad613f345d00e', 'Feel my pain', 'Upon use, deals one heart of damage to Isaac and grants +1.2 damage for the current room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Forget Me Now', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/56/Forget_Me_Now_Icon.png?version=597af1652d43d3b71d3545f7bdced060', 'I don\'t remember', 'When activated, the level ends and is freshly regenerated with new rooms, monsters, and items. Disappears after use');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Forever Alone', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d5/Forever_Alone_Icon.png?version=9436a0e53dceec5a46799a740b3a68f1', 'Attack fly', 'Spawns a blue attack fly that orbits a long distance from Isaac');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Bucket of Lard', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/36/Bucket_of_Lard_Icon.png?version=20ecc09efa6f528db5fc3976e7bf4fc4', 'HP up', 'Grants two empty heart containers and heals Isaac by half a heart, but decreases speed by 0.2');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'A Pony', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/37/A_Pony_Icon.png?version=0220baae26e8b87ef31c42e0be9bd1c3', 'Flight + Dash Attack', 'Passively grants flight and increased speed. Upon use, Isaac charges in the direction the pony is facing, becoming invulnerable during the charge and damaging enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Bomb Bag', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e0/Bomb_Bag_Icon.png?version=0b80aacdad121c6ea548d9d60b2a6a1d', 'Gives bombs', 'Isaac is followed by a small bag that drops a Bomb, 1+1 Free, or a Troll Bomb every 3 rooms');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'A Lump of Coal', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d0/A_Lump_of_Coal_Icon.png?version=864c95ff94c767fc1897647a67105e63', 'My Xmas Present', 'The more distance tears travel, the more damage they will do');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Guppy\'s Paw', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/4c/Guppy%27s_Paw_Icon.png?version=98ef1a08bdfe0e50a68cc600ee9fe9cc', 'Soul converter', 'Removes one red heart container in exchange for gaining 3 Soul Hearts');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Guppy\'s Tail', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/1d/Guppy%27s_Tail_Icon.png?version=34aeb5160c08fbfc759e7d6ea628e8d0', 'Cursed?', 'Increases chance of finding gold chests and normal chests, but reduces chance of finding pickups');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'IV Bag', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/c0/IV_Bag_Icon.png?version=251ce03f49f4161ddc340a48b97ac463', 'Portable blood bank', 'Upon activation, Isaac loses a half-heart (one whole heart in The Womb onwards) for a random amount of coins, similar to the blood donation machine');
