INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Best Bud', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/ef/Best_Bud_Icon.png?version=bac4fdb928ac1b48ea7a498b1427d086', 'Sworn protector', 'When hit, summons a white attack fly similar to Distant Admiration for the current room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lil Brimstone', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/92/Lil_Brimstone_Icon.png?version=5f91df444f0aea1cda7e6b9fc9b6595a', 'Evil Friend', 'A Familiar that shoots a thin Brimstone beam. Must charge like Brimstone before shooting');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Isaac\'s Heart', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/20/Isaac%27s_Heart_Icon.png?version=be78a79cef21d477eb3ba67e336f6c93', 'Protect It', 'Isaac himself is now immune to all damage, and a heart familiar appears and follows Isaac. If the heart is hurt, Isaac is hurt');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lil Haunt', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/89/Lil_Haunt_Icon.png?version=14e9e5b094195e92f0ed34dea3cfa05c', 'Fear him', 'A familiar that damages enemies by chasing them as well as inducing the fear effect');
