INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'How to Jump', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/63/How_to_Jump_Icon.png?version=0937775ba3efadc3b68a595259d8170e', 'It\'s about time you learned how', 'Gives Isaac the ability to jump (over gaps, spikes, etc.)');
