INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Book of Secrets', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/9b/Book_of_Secrets_Icon.png?version=1c0afb984a1a5bfac8815b99dced6eaf', '??????', 'Upon use, gives the effect of Treasure Map, The Compass, or Blue Map for the duration of the current floor');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Box of Spiders', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/5e/Box_of_Spiders_Icon.png?version=509699460ddaea94d715f4f665147916', 'It\'s a box of spiders', 'Summons 2-4 blue spiders');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Red Candle', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/ab/Red_Candle_Icon.png?version=7920bcd8aed5dc84688e76769259cd1b', 'Flame on', 'Throws a persistent flame that deals high damage and diminishes as it damages enemies or blocks shots');
