INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Head of Krampus', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/ed/Head_of_Krampus_Icon.png?version=043de199393295188cf1e5ab53f36cb8', 'Krampus Rage', 'Fires a cross-patterned brimstone shot, just like The Fallen and Krampus shoot');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Butter Bean', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/70/Butter_Bean_Icon.png?version=affd66a1a34b0ed1447c8ff61e5432c2', 'Reusable knock-back', 'Isaac farts and pushes back nearby enemies');
