INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Pandora\'s Box', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/2d/Pandora%27s_Box_Icon.png?version=b423a137266f672d991646d3b3ffd585', '??', 'Upon use, spawns certain pickups or items depending on the floor it is used on. Single-use');
