INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Aquarius', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/59/Aquarius_Icon.png?version=d8f08383a36512b2b99f70b0e72cca6c', 'Trail of tears', 'A trail of blue creep is left behind Isaac, which damages enemies');
