INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', '???\'s Only Friend', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/83/Blue_Baby%27s_Only_Friend_Icon.png?version=32dbe09778bb5db379fe8291bc78fa60', 'Controlled friend', 'Summons a big attack fly that can be controlled with the shooting direction-keys');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Samson\'s Chains', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/23/Samson%27s_Chains_Icon.png?version=7e9ec4a4e29f067b65b99bbbfab9e3a5', 'The ol\' ball and chain', 'Pull a chain and ball behind you that will damage enemies on contact and is able to break stones');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mongo Baby', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/ef/Mongo_Baby_Icon.png?version=0c89305b40448e2e7793341d53c942a8', 'Mongo Friend', 'Summons a familiar that copies the tears of Isaac\'s other familiars');
