INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Undefined', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/5b/Undefined_Icon.png?version=4510687e87c74a319b59f8603fdad227', 'Undefined', 'Teleports Isaac to either the Secret Room, Super Secret Room, Treasure Room, or I AM ERROR');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Scissors', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/83/Scissors_Icon.png?version=fb33a6645026fd992ba0b32fa6a2e9b7', 'Lose your head', 'Upon use, turns Isaac\'s head into a stationary familiar for the current room which fires normal tears that deal 3.5 damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Breath of Life', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a2/Breath_of_Life_Icon.png?version=4b0d5b76b97c49ff4d2dbf027937e8c3', 'Invincibility at a cost', 'Grants a brief moment of invincibility when the charge bar hits zero. If spacebar is held for an additional second after hitting zero, Isaac will take damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Polaroid', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/f4/The_Polaroid_Icon.png?version=13db12682d010b0c6b8333bc55a1bc01', 'Fate Chosen', 'If the character has half a red heart or no red hearts after taking damage, it triggers the Book Of Shadows effect, creating a shield that nullifies all types of damage for 5 seconds. Also allows entrance into The Chest');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Negative', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/0/0a/The_Negative_Icon.png?version=856167b39825c54ef4299a5ef4afaef2', 'Fate Chosen', 'If the character has half a red heart or no red hearts after taking damage, it triggers The Necronomicon effect, damaging all enemies in the room. Also allows entrance into the Dark Room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Ludovico Technique', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/0/05/The_Ludovico_Technique_Icon.png?version=1aeaba9fb9eff2a71790cb87a3ebad55', 'Controlled Tears', 'Instead of firing tears, Isaac controls one floating tear that hover over obstacles. This tear deals constant damage when held in place over an enemy');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Soy Milk', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/39/Soy_Milk_Icon.png?version=36c7be3829ccd7a5826411c45a7706a4', 'DMG Down + Tears Way Up', 'Tears shrink and do much less damage, but fire in very rapid succession');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Godhead', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/3b/Godhead_Icon.png?version=10ef2a0878fb7b4638973ae12b1965c7', 'God tears', 'Increases damage and range while reducing tears and shot speed. Grants homing tears and tears gain a damaging halo');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lazarus\' Rags', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/cf/Lazarus%27_Rags_Icon.png?version=f18438bddc96f52a9b244af0ec6d7498', 'Eternal Life?', 'Upon dying, resurrect as Revived Lazarus with one heart container and Anemic item');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Mind', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/f9/The_Mind_Icon.png?version=6f614bf2ac27b5241518de828638d90c', 'I know all', 'Grants the combined effects of the Blue Map, Treasure Map, and The Compass, revealing the floor layout and all special rooms, including Secret Rooms and Super Secret Rooms');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Body', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/88/The_Body_Icon.png?version=152ad74ec487d3c9ba453dd2b9c076b6', 'I feel all', 'Adds 3 heart containers');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Soul', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b4/The_Soul_Icon.png?version=4dc92ab0f042af8592a63c19165c0bef', 'I am all', 'Grants two soul hearts. Isaac now repels enemy projectiles');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Dead Onion', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/cd/Dead_Onion_Icon.png?version=ef0ca42c1aa6963dbd1b37279ee0cd02', 'Toxic aura tears', 'Grants piercing and spectral tears. Increases tear size (without increasing damage) while greatly reducing range and shot speed');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Broken Watch', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e1/Broken_Watch_Icon.png?version=32b48737e59b8336ae193e9fa1717ef1', 'I think it\'s broken', 'Chance upon entering a room to slow down or speed up every enemy and their attacks. The chance to slow enemies is quite higher than the chance to speed them up');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Boomerang', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a5/The_Boomerang_Icon.png?version=ba655a50abf0afc344cd2d32003cb12b', 'It will never leave you', 'Throws a short-range boomerang which does a bit of damage and has a high chance to stun the enemy for 2 seconds. Can also be used to grab pickups');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Safety Pin', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/fe/Safety_Pin_Icon.png?version=3dd52f9c75a4347721228bb89e98a1ad', 'Evil + range + shot speed up', 'Increases range and shot speed, while adding 1 Black Heart');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Caffeine Pill', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/49/Caffeine_Pill_Icon.png?version=b198306c018d8e1f1bb8a1899d613227', 'Speed up + size down', 'Increases speed and reduces character size. Also gives a random pill');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Torn Photo', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d1/Torn_Photo_Icon.png?version=0ad4c24d1f95cc87603c2a793dbe5995', 'Tears + shot speed up', 'Tears + Shot speed up');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Blue Cap', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/ac/Blue_Cap_Icon.png?version=8222fcad1c91efb18c8ca7b6780d93d4', 'HP + tears up + shot speed down', 'Grants 1 heart container and increases tears, but decreases shot speed');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Latch Key', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/ca/Latch_Key_Icon.png?version=0aeb6e2b55c6072332557ed9cf2ec241', 'Luck up', 'Increases Luck by 1, grants 1 Soul Heart and spawns 2 Keys');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Match Book', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b4/Match_Book_Icon.png?version=6dbc8b43ac35372c5e7956fd3d177a63', 'Evil up', 'Grants Isaac 1 Black Heart and 3 bombs. Can also give troll bombs');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Synthoil', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/6c/Synthoil_Icon.png?version=4aaeefd8bf8b6d67e9b8bcb93b93756f', 'DMG up + range', 'Increases damage by 1, range by 5.25, and tear height by 0.5');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'A Snack', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/7e/A_Snack_Icon.png?version=b80e21b1d5345f3106142bb070c9ef27', 'HP up', 'Grants one full red heart container');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Diplopia', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/25/Diplopia_Icon.png?version=2e01693338aa2e3a1b0f603c28b625f5', 'Double item vision', 'Duplicates all pickups and item pedestals in the room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Placebo', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b4/Placebo_Icon.png?version=9c26bc1dff5be684b21258966de1287e', 'Pill mimic', 'When used, copies the effect of the pill Isaac is currently holding without consuming it. Does not copy the effects of cards or runes');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Wooden Nickel', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/6f/Wooden_Nickel_Icon.png?version=612c7521b923152ff86c5cc8793e41d8', 'Flip a coin', 'When used, has a 50% chance to drop one random type of coin (penny, nickel, or dime)');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Toxic Shock', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/ae/Toxic_Shock_Icon.png?version=5f12a85c66c941e7e76794ac54f92e04', 'Mass poison', 'Upon entering a room, poisons all enemies, dealing double Isaac\'s tear damage. Killing enemies leaves a small, short lasting pool of green creep on the ground');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mega Bean', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/3f/Mega_Bean_Icon.png?version=daa8f2975a03828474c0a128fe72a844', 'Giga fart!', 'When used, the Mega Bean will freeze all enemies in the current room for a couple of seconds, deal 5 damage and poison any enemies near Isaac, and send a wave of spikes that deals 10 damage across the room in the direction Isaac is facing');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Glass Cannon', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/87/Glass_Cannon_Icon.png?version=a1b67eada11e88cbec9897e1632e3b87', 'Be gentle...', 'When used, reduces Isaac\'s health to half a heart (including soul and black hearts) and fires one large piercing spectral tear that does a lot of damage. The damage done by Glass Cannon scales with Isaac\'s damage (at base damage it does 45 damage)');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Bomber Boy', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/12/Bomber_Boy_Icon.png?version=e7a2126116a83cb04a624f9414c1f437', 'Explosive blast!', '+5 bombs. Isaac\'s bombs will now explode in a large cross-shaped pattern (roughly 5 bombs explosions wide). This also affects troll bombs');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Crack Jacks', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/8f/Crack_Jacks_Icon.png?version=51a3c3b56f11781600924dbb5a366bd4', 'Don\'t swallow the prize!', 'Adds one heart container and drops a random trinket');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mom\'s Pearls', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/c8/Mom%27s_Pearls_Icon.png?version=978c77a5b3dae44916991a2eeaf4e07d', 'Range + luck up', '+1.25 Range Up. +0.5 Tear Height. +1 Luck Up');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Car Battery', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a0/Car_Battery_Icon.png?version=86561f3ffd576705dc53a859ee286a4e', 'Double charge!', 'Causes Isaac\'s activated item to activate twice on each use');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Box of Friends', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/2d/Box_of_Friends_Icon.png?version=41d4ec28a581914119683df839c091fb', 'Double your friends', 'Upon use, duplicates all of Isaac\'s familiars for the current room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'The Wiz', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/f4/The_Wiz_Icon.png?version=b4ac8978a02febb98d3a41ad3a5f3ae3', 'Double wiz shot!', 'Tears simultaneously fire out of both eyes diagonally, similar to the effect of the R U A Wizard? pill. Isaac\'s tears also travel above all obstacles');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', '8 Inch Nails', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/3d/8_Inch_Nails_Icon.png?version=8b65d490e99f38a130169555971f2d97', 'Stick it to \'em!', '+1.5 Damage Up. Replaces Isaac\'s tears with nails, which have increased knockback');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Incubus', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/cd/Incubus_Icon.png?version=03947c9d7bc2a111ff55381e54f921c2', 'Dark friend', 'A demonic familiar that follows Isaac and shoots tears identical to Isaac\'s in terms of damage, range, fire rate, and effects');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Fate\'s Reward', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/3c/Fate%27s_Reward_Icon.png?version=cad60f4a89d17948b6f8f5617a8ee25e', 'Your fate beside you', 'A blue head familiar that will follow Isaac and fire tears copying his tear effects and damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lil Chest', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d5/Lil_Chest_Icon.png?version=4a945ede084d2e175013e3e8b1375082', 'What\'s in the box?', 'Spawns a chest familiar that follows Isaac and drops a random pickup every few rooms');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Sworn Protector', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/0/03/Sworn_Protector_Icon.png?version=45fb2653878f9319dc399f9b5e502fe2', 'Protective friend', 'An orbital angel which does 7 contact damage per tick and blocks enemy shots. Upon blocking 10 shots in a room it drops an eternal heart');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Friend Zone', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/13/Friend_Zone_Icon.png?version=ef3ec49f50d489fc679fe3f582ba3fd1', 'Friendly fly', 'A white orbiting fly which deals 3 contact damage per tick to enemies. This familiar orbits Isaac at a middle length distance somewhere between Distant Admiration and Forever Alone');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lost Fly', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/c1/Lost_Fly_Icon.png?version=0af37c4c24a5285297ed96975ab56b37', 'Lost protector', 'A familiar fly that moves in a straight line and attaches itself to the first obstacle or wall it comes into contact with in each room. If any enemies walk into its path, it will deal 7 contact damage per tick. If the obstacle it is attached to is destroyed then it will continue rolling in the direction it was previously travelling until it meets another wall or obstacle');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Scatter Bombs', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/51/Scatter_Bombs_Icon.png?version=9de1d4192a31de36c2923af4118b67e2', 'We put bombs in your bombs!', 'Adds 5 bombs. Isaac\'s bombs explode into 2-4 tiny bombs');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Sticky Bombs', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/0/00/Sticky_Bombs_Icon.png?version=ecff130974cfffe5fcd203a351a0ea2a', 'Egg sack bombs!', '+5 bombs. Bombs stick to enemies and spawn 2-4 blue spiders if they kill an enemy');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Epiphora', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/92/Epiphora_Icon.png?version=e912ffb0996a026f63a609da5fb33e61', 'Intensifying tears', 'Shooting in one direction increases Isaac\'s rate of fire until the cap is reached. As soon as Isaac changes direction or stops shooting, rate of fire is reset to its normal amount');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Continuum', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d4/Continuum_Icon.png?version=94e218772ad58aaad25de12354ff0e81', 'Transcendent tears', '+2.25 Range Up. +1.5 Tear Height. Isaac\'s tears travel through walls and appear out of the opposite wall. Isaac\'s tears also travel over rocks and objects');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mr. Dolly', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/27/Mr._Dolly_Icon.png?version=18197efa7eb06a95f1b4c4ec9d8b2da5', 'Range + tears up', 'Tears and Range up. Spawns 3 random types of hearts on the floor when picked up');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Curse of the Tower', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/3a/Curse_of_the_Tower_Icon.png?version=d243b3b6b00b711fd80fb54e65694c14', 'You feel cursed...', 'Causes Isaac to spawn 6 troll bombs on the floor every time he gets hit, similar to the Anarchist Cookbook mechanic');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Charged Baby', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b4/Charged_Baby_Icon.png?version=5461fceaafbb336e704c069c4a82679c', 'Bbbzzzzzt!', 'A familiar that drops a Little Battery every 4 rooms. Has a small chance to freeze all enemies in the room when hit');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Dead Eye', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e0/Dead_Eye_Icon.png?version=ef5a003582b88d9e25cba550661eddc0', 'Accuracy brings power!', 'Isaac\'s damage increases each time an enemy is hit. Damage is reset upon missing a shot');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Holy Light', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/4e/Holy_Light_Icon.png?version=221d842c0f3a801e4a0c1446123d9b01', 'Holy shot!', 'Isaac has a random chance to fire a Holy tear, which, when it hits an enemy, will spawn a Crack the Sky-style light beam on the same enemy, dealing damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Host Hat', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/65/Host_Hat_Icon.png?version=b3ddf75c1b3da6b956102cecdb9887a4', 'Nice hat!', 'A host familiar that sits on Isaac\'s head and has a random chance to block enemy shots and retaliate with 3 tears. Grants immunity to explosions');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Restock', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a6/Restock_Icon.png?version=1468ad924ab9282af947ad7d04d05dc0', 'Never ending stores!', 'Causes shops to instantly restock their items when they are bought. Granted by default for every character in Greed Mode');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Bursting Sack', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/ee/Bursting_Sack_Icon.png?version=1edd283f5f4cf6971c61f4215183cfb2', 'Spider love', 'Spider enemies no longer target or deal contact damage to Isaac');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'No. 2', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a5/No._2_Icon.png?version=d233e68ccc97831a7398cc45d9e2d637', 'Uh oh...', 'Continuously firing tears for several seconds causes Isaac to drop a Butt Bomb. Butt Bombs explode for 50 damage like normal bombs, but also deal 10 damage to all enemies in the room and daze them for a short period of time');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Pupula Duplex', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/f2/Pupula_Duplex_Icon.png?version=f6fe8d5d7fe7c34c325387571d0cf6f6', 'Wide shot', 'Transforms Isaac\'s tears into a wide arc shape which gives them a much larger hitbox. Gives Isaac spectral tears which allows them to travel through objects in the environment (i.e. rocks)');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Pay to Play', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/41/Pay_to_Play_Icon.png?version=13d0cfc32e1c574d0f5b37478de3554e', 'Money talks', 'This item turns all doors that require a key into doors which need a coin to enter instead. This affects shops, item rooms, libraries and other key doors');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Eden\'s Blessing', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/f9/Eden%27s_Blessing_Icon.png?version=abf32fac601d6780caa505688f10c2f2', 'Your future shines brighter', '+0.7 tears up. Grants a random item at the start of the next run');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Friendly Ball', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/5c/Friendly_Ball_Icon.png?version=e1af2bf47cf38ff89967e8c3c5ffd730', 'Gotta fetch \'em all!', 'Throwing the ball at an enemy instantly kills it and captures it. The captured enemy can later be released and will be charmed until it dies, persisting between floors');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Tear Detonator', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a2/Tear_Detonator_Icon.png?version=6682852a0cd57fc3e8e22be0f87fc2d6', 'Remote tear detonation!', 'When used, will detonate any tears currently on the screen and cause each one to split into 6 more tears which will fire in a circle, similar to Tammy\'s Head');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lil Gurdy', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/ce/Lil_Gurdy_Icon.png?version=ea7f9eafed4654676ef36588481ff2e2', 'A gurd of your own!', 'Gives Isaac a Gurdy familiar that will charge around the room dealing contact damage to enemies. Lil Gurdy is charged by holding down a fire button and letting go. The longer the button is held, the faster Lil Gurdy will fling itself across the screen. Lil Gurdy can pass over objects and obstacles in the room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Bumbo', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a7/Bumbo_Icon.png?version=2568679c3ecdcdbcf2240ce07dd62469', 'Bumbo want coin!', 'Spawns a beggar familiar which floats around the room and picks up any nearby coins, leveling up after collecting several coins. At level 2, Bumbo gains a body. At level 3, it begins to fire tears. At level 4, Bumbo chases enemies slowly, dealing contact damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'D12', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/ac/D12_Icon.png?version=1a42729bceb1c93885524dee7d991b15', 'Rerolls rocks', 'Re-rolls all rocks in the current room into other random objects, such as bomb rocks, metal blocks, TNT, or poops');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Censer', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/26/Censer_Icon.png?version=da5768d63369193dc01de0ad7db561b4', 'Peace be with you', 'Gives Isaac a golden censer familiar which creates a huge aura of light that slows down any enemies and enemy shots inside it');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Key Bum', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/0/0e/Key_Bum_Icon.png?version=2c7cc4fc3280af4550dbb3989daaf31f', 'He wants your keys!', 'Spawns a portable key master that follows Isaac and collects keys, giving random chests in return');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Rune Bag', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/a4/Rune_Bag_Icon.png?version=b6339f62a733c41e5af08a7242a37906', 'Rune generator', 'A bag that follows Isaac and drops a random rune every 3 rooms');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Seraphim', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/5/54/Seraphim_Icon.png?version=98d4316aef28cb906c4dd9f16ed8075d', 'Sworn friend', 'An angel familiar which follows Isaac and fires homing tears which deal 10 damage at -25% shot speed and a reduced fire rate');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Betrayal', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/1c/Betrayal_Icon.png?version=9ee3db065b00e2a7cd166a47a9eccbda', 'Turn your enemy', 'Every time Isaac takes damage, a charm effect is applied to every enemy in the room, causing them to attack each other rather than Isaac');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Zodiac', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/a/ab/Zodiac_Icon.png?version=43d796976edd407ee876ce75a1421e0e', 'The heavens will change you', 'Zodiac will give Isaac a random zodiac item effect that changes after every floor');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Serpent\'s Kiss', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/be/Serpent%27s_Kiss_Icon.png?version=64c5949b911bdecd4ac11cafdfe903c6', 'The kiss of death', 'Tears now have a random chance to apply a poison effect, causing 4 or 6 damage per tick to enemies (Similar to The Common Cold item). Isaac now deals poison damage on contact with enemies similar to The Virus item (Damage over time 4 or 6 per tick). Enemies that were poisoned have a chance to drop a black heart when killed');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Marked', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/90/Marked_Icon.png?version=b0264c4c435bb154f71ddc1d5277e536', 'Directed tears', 'Isaac will now fire tears automatically directed at a red target on the ground which is controlled by the player. This allows for a full 360 degree tear firing radius. Can be overridden by other tear modifiers such as Mom\'s Knife or Brimstone');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Tech X', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e6/Tech_X_Icon.png?version=4c69b541443aa995f08a0804c18f92a1', 'Laser ring tears', 'Tears are replaced with laser rings that pierce through enemies and deal damage to enemies within them. Can be charged to increase the size of the ring');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Ventricle Razor', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/1c/Ventricle_Razor_Icon.png?version=1a7c82eb902b0756fe9c11e7630319ce', 'Short cutter', 'Can be used to place an orange portal and a blue portal. Upon entering a portal, Isaac is teleported to the other portal');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Tractor Beam', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/45/Tractor_Beam_Icon.png?version=d28672b24e315588629f71e7c2a48572', 'Control your tears', 'Isaac emits a beam from his face. Isaac\'s tears travel in the path of the beam');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'God\'s Flesh', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/92/God%27s_Flesh_Icon.png?version=3a8711847508fafbd2493b839c355b9a', 'Shrink shot', 'Tears now have a random chance to apply a shrinking effect, causing enemies to shrink in size and also run away from Isaac. Shrunk enemies can be crushed and killed by walking over them');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Maw of the Void', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/80/Maw_of_the_Void_Icon.png?version=97d7fc0e4c5f8d1eebf416535566d45e', 'Consume thy enemy!', '+1.0 Damage Up. After continuously firing tears for 2.5 seconds, a red cross appears on Isaac\'s head that, upon releasing the fire button, creates a black ring that rapidly deals damage equal to Isaac\'s tear damage to any enemies in contact with it');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Spear of Destiny', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e0/Spear_of_Destiny_Icon.png?version=5586fc31288071344a9d2ad78b1a5ba3', 'Your destiny', 'In addition to tears, Isaac now holds a spear in front of him which deals damage equal to 2 times his tear damage, and occasionally inflicts fear. The spear cannot be thrown, but deals damage while it is in contact with an enemy');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Explosivo', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/cf/Explosivo_Icon.png?version=f86591c2b680354de779eb031ab18d0b', 'Sticky bomb shot!', 'Isaac\'s tears have a chance to stick to enemies and explode');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Chaos', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/79/Chaos_Icon.png?version=50b87a14b546d84d0703d0f1443c5449', '!!!', 'Chaos causes items to be chosen from random item pools. When picked up, drops between 1-6 random pickups on the floor');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Spider Mod', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/6f/Spider_Mod_Icon.png?version=0c8484673644dc838df1a7d3b8d3837c', 'Mod buddy!', 'Displays Isaac\'s tear damage and enemy health bars. Spawns a gray spider familiar that wanders around and inflicts a random status effect on any enemies it comes into contact with. Occasionally spawns a blue spider upon clearing a room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Farting Baby', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/31/Farting_Baby_Icon.png?version=d8fa8f433c3ada4c9c39412bfb7526ac', 'It farts!', 'A familiar that follows Isaac and blocks tears. If a tear hits Farting Baby, there is a high chance for it to fart, which will either knock back, poison, or charm all nearby enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'GB Bug', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d1/GB_Bug_Icon.png?version=e67a70f7faa4c102b25fd720ff78da69', 'Game breaking bug, right away!', 'A glitch familiar that bounces around the room and applies a random status effect to any enemies it comes into contact with and has a chance to re-roll pickups that it passes over');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'D8', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/17/D8_Icon.png?version=bcc4579cfba21b53dd7e17b4e1594544', 'Reroll stats', 'Re-rolls Isaac\'s speed, range, tears, and damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Purity', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/0/0b/Purity_Icon.png?version=efd3fb22187a9233835974808361e76b', 'Aura stat boost', 'This item will boost one of Isaac\'s stats depending on the color of the aura around him. The aura will disappear upon taking damage and will reappear with a random color upon entering a new room. A red aura boosts damage, a blue aura boosts rate of fire, a yellow aura boosts speed, and an orange aura boosts range');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Athame', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/91/Athame_Icon.png?version=951328e4ccc8af64e67f8403f36dad2f', 'Call to the void', 'Whenever Isaac takes damage, a black ring briefly appears around Isaac, dealing damage to all nearby enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Empty Vessel', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/94/Empty_Vessel_Icon.png?version=5a18c08ffa3643d919d4ac3b82a44583', 'I reward an empty vessel', 'While Isaac has no red hearts, this item grants flight and a random chance to activate an invincibility shield for a few seconds. If Isaac gains any red hearts, these benefits will disappear');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Evil Eye', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b3/Evil_Eye_Icon.png?version=c77a7b23fbbb6b1df434c3d738d76910', 'Eye shot!', 'While firing tears, Isaac has a chance to fire an eye across the screen with a very slow shot speed, which will fire tears of its own in the same direction as Isaac. The eye will be destroyed if it comes into contact with an enemy or obstacle');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lusty Blood', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/43/Lusty_Blood_Icon.png?version=3034b022c6abf28a610c21202a4c8128', 'Their blood brings rage!', 'Each time Isaac kills an enemy, Isaac\'s damage increases for the current room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Cambion Conception', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/1/1b/Cambion_Conception_Icon.png?version=d37f1e36e8da3352402ed72e928e9282', 'Feed them hate', 'After taking damage a certain amount of times, a permanent demonic familiar spawns');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Immaculate Conception', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/bd/Immaculate_Conception_Icon.png?version=82ec4dd15267f22d5733f1a2db90806f', 'Feed them love', 'Upon picking up a certain amount of health, spawn a permanent angelic familiar');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'More Options', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e3/More_Options_Icon.png?version=d4897bb9c108e022a16316f41a8fbf79', 'There are even more options!', 'Two items spawn in every Treasure Room. Only one can be take, the other will disappear');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Crown of Light', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/d9/Crown_of_Light_Icon.png?version=c2a34ecdcefd9652d5649728b176b297', 'The untainted gain power', 'Gives Isaac a crown that glows blue when active, and turns gray when inactive. While Isaac has no empty red heart containers, Isaac\'s tears are replaced with blue diamond tears that deal double damage. Regardless of health, the crown will deactivate for the current room when Isaac takes damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Deep Pockets', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/eb/Deep_Pockets_Icon.png?version=d54e62757d6eecf7864496054675949b', 'More stuff to carry!', 'Allows Isaac to carry two cards or pills (or a combination of both) at the same time');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Succubus', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/ce/Succubus_Icon.png?version=095536802d3bfa339e4541c7fcc64de2', 'Damage booster', 'Spawns a flying familiar that bounces around the room with a black aura, dealing rapid damage to any enemies inside it. While standing in the aura, Isaac gains a 1.5x tear damage multiplier');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Fruit Cake', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/8f/Fruit_Cake_Icon.png?version=c0faac8aade5d8f21f47efaf84806d88', 'Rainbow effects!', 'Isaac\'s tears gain a random effect that changes every shot');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Teleport 2.0', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/76/Teleport_2.0_Icon.png?version=8107ccae2f056e9ab2121527f4efce08', 'I-Teleport!', 'When used, teleports Isaac to a random room that has not been explored yet. If all rooms are explored, including secret rooms, it will teleport Isaac to the Devil or Angel room. If all rooms are explored, including the Devil or Angel room, it will teleport Isaac to the I AM ERROR room. If used in a room with enemies, it will teleport Isaac out and back into the exact same room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Black Powder', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b0/Black_Powder_Icon.png?version=db2dc8e75e6ad0b906a18dbd7bcb3256', 'Spin the black circle!', 'Isaac leaves a trail of black powder on the floor. Walking in a circle will spawn a pentagram symbol on the floor, which deals 10 damage per tick to any enemies inside it');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Kidney Bean', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/95/Kidney_Bean_Icon.png?version=d3cae8e87dad982dedb0865ddc0d2d8c', 'Love toots!', 'Upon use, Isaac farts, charming all nearby enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Glowing Hour Glass', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/fa/Glowing_Hour_Glass_Icon.png?version=ffddbf439eb52a8782f84d99e63aeb7b', 'Turn back time', 'When used, this item will rewind time, put Isaac back in the previous room, and undo anything that happened in the current room. Any health, pickups, or items gained or lost will be reset back to the previous state');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Circle of Protection', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/77/Circle_of_Protection_Icon.png?version=48061f4aa466325a321b136657399512', 'Protect me from myself', 'Surrounds Isaac with a large white halo around him that pulsates. With each pulse, the halo deals contact damage to enemies equal to Isaac\'s damage. Each pulse also converts any enemy shots touching the ring into friendly purple homing tears');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Sack Head', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/4/48/Sack_Head_Icon.png?version=9009fa9aee1041467ce3a85b7105e288', 'More sacks!', 'Greatly increases the chance to find sacks');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Night Light', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/3b/Night_Light_Icon.png?version=4f6f72e4f77ccc75879d96e370a5bb37', 'Scared of the dark?', 'A cone of light shines in the direction Isaac is moving that slows enemies and enemy shots');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Obsessed Fan', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/c5/Obsessed_Fan_Icon.png?version=8d83ec42d802e0fa17841451c5700f85', 'Follows my every move...', 'A purple fly familiar that follows Isaac\'s movements on a 3 second delay and deals contact damage to enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mine Crafter', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/23/Mine_Crafter_Icon.png?version=00a29e31b0a3c92abd788243b1839694', 'Booom!', 'When used, places an explosive TNT barrel that can be pushed and used to destroy rocks or damage enemies. If used a second time in the same room while the previous TNT barrel is still intact, it will remotely explode the TNT');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'PJs', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/97/PJs_Icon.png?version=a2670533b1fae835f57ed845eaa8e536', 'You feel cozy', 'Grants four soul hearts and fully restores Isaac\'s red health');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Head of the Keeper', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/eb/Head_of_the_Keeper_Icon.png?version=356be163e715315abb9c419e906d8d0d', 'Penny tears!', 'Isaac\'s tears become coins and now have a chance to drop pennies on the floor upon successfully hitting an enemy');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Papa Fly', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/95/Papa_Fly_Icon.png?version=47140a6861c8d13ef8f508700451a963', 'Turret follower', 'A blue fly familiar follows Isaac\'s movements on a 3 second delay. Papa Fly will fire tears at nearby enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Multidimensional Baby', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/0/0b/Multidimensional_Baby_Icon.png?version=ab55684bc29bd6710cb546556ed3fe97', 'ydduB Buddy', 'Spawns a familiar that will follow Isaac\'s movements on a 2.5 second delay. Tears that pass through the baby will be doubled and increase in speed');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Glitter Bombs', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/66/Glitter_Bombs_Icon.png?version=408abbafdf1c6aaa65b1a3ba4c27e1f8', 'Prize bombs!', 'Gives Isaac 5 bombs, and makes Isaac\'s bombs have a chance to drop random pickups or charm enemies when they explode');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'My Shadow', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/98/My_Shadow_Icon.png?version=4550ec599bd3944b5378d17cb0f3fb44', 'Me! And my shaaaadow!', 'Each time Isaac takes damage, inflicts fear on all enemies in the room and spawns a black Charger familiar that will attack enemies. The Charger will be killed if it is damaged too much');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Jar of Flies', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/63/Jar_of_Flies_Icon.png?version=8c1f9c5126fae87b09842bd1edff4397', 'Gotta catch \'em all?', 'Every time Isaac kills an enemy, a fly will be added to the jar, up to a maximum of 20. Using the Jar of Flies will release all the flies that are currently in the jar as friendly blue flies that will damage enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lil\' Loki', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/6b/Lil%27_Loki_Icon.png?version=b7aa6fcd81b6842ec84951d33ba72ab4', '4-way buddy!', 'A Loki familiar that follows Isaac and shoots 4 tears in a cross pattern');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Milk!', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/e6/Milk%21_Icon.png?version=ee6805c710774c08faf96bc7ccaa5ef6', 'Don\'t cry over it...', 'A glass of milk follows Isaac and spills on the floor upon taking damage. After the milk spills, Isaac\'s tear delay is decreased by 2 for the rest of the room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'D7', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/cc/D7_Icon.png?version=dbd3bfca25750bfc1568907223354b93', 'Reroll rewards!', 'Restarts the current room and brings back all enemies, allowing more rewards to be acquired by completing the room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Binky', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b3/Binky_Icon.png?version=fb08c19bc643bea505f18233c3a27ff2', 'Memories...', 'Tears up, adds 1 soul heart, and decreases Isaac\'s size');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mom\'s Box', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/b5/Mom%27s_Box_Icon.png?version=e6c73175cb1451444c4da33592dca72a', 'What\'s inside?', 'When used, Mom\'s Box will drop a random trinket on the ground. While held, increases luck by 1 and doubles the effects of trinkets');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Kidney Stone', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/ef/Kidney_Stone_Icon.png?version=8713632f32b721de867dabf6337ee20e', 'Matt\'s kidney stone', 'Randomly while firing tears, Isaac will stop firing and turn red, where he will charge and release a lot of \'tears\' in one go. In the burst of tears is a kidney stone which deals a lot of damage');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Mega Blast', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/2/2d/Mega_Blast_Icon.png?version=928cecfe3baa9f6b6c7034c0c564609f', 'Laser breath', 'Upon use, Isaac fires a laser similar to the one used by Mega Satan. This laser deals massive damage, lasts for approximately 15 seconds, and persists between rooms. Isaac is pushed backwards by the force of the laser while it is active');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Dark Prince\'s Crown', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/b/bc/Dark_Prince%27s_Crown_Icon.png?version=3413a75b0ea9019a35a52e9cbb01a27b', 'Loss is power', 'Increases range, tears, and shot speed when at exactly one red heart');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Apple!', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/e/ef/Apple%21_Icon.png?version=8446d1e6f879290466c43bf33194032c', 'Trick or treat?', 'Increases tears. Adds a chance to fire a high-damage razor blade');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Lead Pencil', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/91/Lead_Pencil_Icon.png?version=e987a8e2f2b517cded041cff812a6b47', 'He\'s a bleeder!', 'After every 15 tears fired, Isaac will fire a high-damage cluster of tears');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Dog Tooth', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/d/db/Dog_Tooth_Icon.png?version=24bd7346b1084275c602f9d5b2e1c04b', 'Bark at the moon!', 'Increases damage and speed. Upon entering a room adjacent to a secret room, a howl sound effect will play');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Dead Tooth', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/9/90/Dead_Tooth_Icon.png?version=7427fc2a437cbf9e80587b847206a39f', 'Halitosis', 'While firing, Isaac gains a green aura that poisons nearby enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Linger Bean', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/7/78/Linger_Bean_Icon.png?version=bc5a030044f38b98fd865104d56a5415', 'Crying makes me toot', 'Adds a chance to fire a gas cloud that damages nearby enemies');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Shard of Glass', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/6/6b/Shard_of_Glass_Icon.png?version=dc25682b2f7528070df1bcb7d40f5021', 'Blood and guts!', 'Upon taking damage, Isaac has a chance to drop a full red heart, gain 5 range, and leave a trail of damaging red creep for the current room');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Metal Plate', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/f/ff/Metal_Plate_Icon.png?version=799b3f6152a1bba308a705268dbe9b27', 'It itches...', 'Adds a chance to reflect enemy shots. Grants one soul heart');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Eye of Greed', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/38/Eye_of_Greed_Icon.png?version=4f1650fb4951150910fe03865a396097', 'Gold tears!', 'Adds a chance to fire tears that temporarily stun enemies and turn them into gold. Any enemy killed while in this state will drop coins');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Tarot Cloth', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/3/31/Tarot_Cloth_Icon.png?version=5e71be9f47734f5eabbcdfb176e8e0b4', 'I see the future', 'Drops a random card or rune. Doubles the effects of cards and runes');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Varicose Veins', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/8/80/Varicose_Veins_Icon.png?version=6a6f00d6ec4981959c283e6069f5ad38', 'I\'m leaking...', 'Taking damage releases a ring of 10 high damage tears');
INSERT INTO `Isaac`.`item` (`id`, `name`, `icon`, `quote`, `description`) VALUES ('', 'Compound Fracture', 'https://gamepedia.cursecdn.com/bindingofisaacre_gamepedia/c/c7/Compound_Fracture_Icon.png?version=9e44315283cc03d73df7c0f4246fff00', 'Bone tears!', 'Increases range. Tears turn into bones, which shatter into 1-3 bone shards upon hitting an enemy or obstacle');
